        setSaving(true);
        axios.post("http://localhost:8000/user/create",{
            name:name,
            last_name:lastName,
            email:email
        }).then((response)=>{
            setSaving(false);
            listUsers();
        })
    }
    function listUsers(){
        axios.get("http://localhost:8000/user/list").then((response)=>{
            setUsers(response.data)
        })
    }
