import { TextField } from "@mui/material";
import axios from "axios";
import { useEffect, useState } from "react";
import LoadingButton from '@mui/lab/LoadingButton';

function CreateUserForm(){

    const [title,setTitle]=useState("");
    const [content,setContent]=useState("");
    const [project,setProject]=useState("");
    const [startDate,setStartDate]=useState("");
    const [endDate,setEndDate]=useState("");
    const [saving,setSaving]=useState(false);
    const [posts,setPosts]=useState([]);
    function save(){
        setSaving(true);
        axios.post("http://localhost:8000/user/create",{
            name:name,
            last_name:lastName,
            email:email
        }).then((response)=>{
            setSaving(false);
            listPosts();
        })
    }
    function listPosts(){
        axios.get("http://localhost:8000/post/list").then((response)=>{
            setPosts(response.data)
        })
    }
    useEffect(()=>{
        listPosts();
    },[])
    return (
        <div style={{marginTop:30}}>
            <div>
            <TextField id="outlined-basic" label="Name" variant="outlined" value={name} onChange={(event)=>{
                const {value}=event.target
                setName(value)
            }} />
            <TextField id="outlined-basic" label="Last Name" variant="outlined" value={lastName} onChange={(event)=>{
                const {value}=event.target
                setLastName(value)
            }}/>
            <TextField id="outlined-basic" label="Email" variant="outlined" value={email} onChange={(event)=>{
                const {value}=event.target
                setEmail(value)
            }} />
            <LoadingButton loading={saving} variant="contained" onClick={save}>Save</LoadingButton>
            </div>
            <div>
                {posts.map((post:any)=>{
                    var a = post.status
                    var status = ""
                    if (a==true) {status="Completado"}
                    else {status="No completado"}
                    return <>
                        <p>{post.title} {post.content} {post.project} {post.startDate} {post.endDate} {post.hoursPerDay} {status}</p>
                    </>
                })}
            </div>

        </div>
    );

}

export default CreateUserForm;
